var variable = 0.0;
var yVar = 0;
var width,height;


window.onload = function(){
	height = window.innerHeight;
	width = window.innerWidth;
	yVar = height/2;

	var canvasElement = document.getElementById("myCanvas");
	canvasElement.height = height;
	canvasElement.width = width;

	paper.setup("myCanvas");

	var points = createTriangles(Logo,(width/2)-221,(height/2)-300);

	view.draw();

	var tool = new Tool();
	tool.onMouseDown = function(event){
		for(var a = 0; a < points.length; ++a){
			points[a].segments[1].point.x = event.point.x;
			points[a].segments[1].point.y = event.point.y;
		}
		view.onFrame = null;
	}

	view.onFrame = function(event) {
		variable += .05;
		var xVar = (Math.sin(variable/3)*width/2)+width/2;
		var yVar = (Math.cos(variable)*height/2)+height/2;

		for(var a = 0; a < points.length; ++a){
			points[a].segments[1].point.x = xVar;
			points[a].segments[1].point.y = yVar;
		}
	}

	var tool = new Tool();
	tool.onMouseMove = function(event){
		yVar = event.point.y;
	}
}



function createTriangles(points,x,y){
	var paths = [];
	var path;

	var prevPoint = points[0];
	var nextPoint = null;

	while(points.length > 1){
		nextPoint = findClosest(points,prevPoint);
		prevPoint = points[nextPoint];

		path = new Path();
		path.add([prevPoint[0]+x-30,prevPoint[1]+y]);
		path.add([prevPoint[0]+x,prevPoint[1]+y+30]);

		path.strokeColor= "black";
		path.strokeColor.alpha = 0.5;

		points.splice(nextPoint,1);
		paths.push(path);
	}
	return paths;
}



function findClosest(points, currentPoint){
	var smallest = 99999999;
	var smallestValue = 99999999;
	var temp = 0;
	for(var a = 0; a < points.length; ++a){
		temp = subtract(points[a],currentPoint);
		if(temp < smallestValue){
			smallestValue = temp;
			smallest = a;
		}
	}

	return smallest;
}

//subtract 1 from 2
function subtract(point1, point2){
	var x = point2[0]-point1[0];
	var y = point2[1]-point1[1];
	if(x < 0){
		x = x*-1;
	}

	if(y < 0){
		y = y*-1;
	}

	return x+y;
} 